import React from 'react';

function InputArea({ step, instructions, type, id, placeholder, label, rows }) {
  return (
    <div className="mb-3">
      {/* <h4 className="step">{step}</h4> */}
      <p className="instructions"> <span class="step bold">{step}</span> {instructions}</p>
      {type === 'textarea' ? (
        <textarea
          className="form-control"
          id={id}
          rows={rows || 3}
        ></textarea>
      ) : (
        <input
          type={type || 'text'}
          className="form-control"
          id={id}
          placeholder={placeholder || ''}
        />
      )}
      <label htmlFor={id} className="form-label description">
        {label || ''}
      </label>
    </div>
  );
}

export default InputArea;
